import React from 'react'
import {View, Text, StyleSheet} from 'react-native'
export default function Card(props){
    return(
        <View style={{ width: '40vw', height: '15vh', backgroundColor: '#fff', borderRadius: '2vw'}}>
            <Text style={{color: '#000', fontSize: '4vh'}}>
                {props.itemHeading}
            </Text>
        </View>
    )
}
