import React from 'react'
import {View, Text, StyleSheet} from 'react-native'
export default function Card(props){
    return(
        <View style={styles.card}>
            <Text style={styles.heading}>
                {props.itemHeading}
            </Text>
            <Text style={styles.content}>
                {props.content}
            </Text>
        </View>
    )
}
const styles = StyleSheet.create({
    card: {
        width: '30vw',
        height: '24vh',
        backgroundColor: '#fff',
        borderRadius: '2vw',
        boxShadow: '4vw 4vw 4vw rgba(0,0,0, 0.5)',
        margin: '2vw',
        padding: '2vw',
        justifyContent: 'center',
        alignItems: 'center'
    },
    heading: {
        color: '#000',
        fontSize: '3vh'
    },
    content: {
        color: '#555',
        fontSize: '2vh'
    }
})
