import React, { Component } from 'react';
import { Linking, StyleSheet, Text, View, ScrollView, Image, TextInput, Button, TouchableOpacity } from 'react-native';
import Separator from './components/Separator.js'
import Card from './components/Card.js'
import menu from "./res/menu.png"
import cart from './res/cart.jpg'
import user from './res/Username.jpg'
import icon from './res/icon.png'
import coo from './res/coo.jpeg'
import cio from './res/cio.jpg'
import ceo from './res/ceo_blair.jpg'
import cto from './res/cto.jpg'
import cco from './res/cco.jpg'
import git from './res/git.png'
export default class App extends Component {
  constructor(props){
    super(props)
    this.state = {
        showDepartments: true,
        showDiversification: true,
        showTopBar: true,
        about: {
            goal: 'To establish an online service that connects our clients with agriculture suppliers from allover the worls',
            mission: 'To supply agriculture globally at affordable rates',
            target: 'Europe, North America, Asia',
            contacts: {
                linkedin: 'silas-nyamwange-042761228',
                facebook: 'silas.dopelimitlesity',
                instagram: 'silas.n_'
            },
            location: 'online',
            availability: {
                delivery: 'after 2PM',
                orders: '24 hours'
            },
            year: '2021',
            sponsor: 'Nyamwas-ENT',
            licence: {
                copywright: '2021',
                company: 'MIT licence'
            }
        },
        separators: {
            about: 'ABOUT',
            diversifications: 'DIVERSIFICATION',
            departments: 'DEPARTMENTS',
            features: 'FEATURED',
            forms: 'FORMS',
            records: 'RECORDS',
            feedback: 'FEEDBACK'
        },
        diversifications: [
            {content: 'agriPay', description: 'Agripay', image: git},
            {content: 'agriNews', description: 'Agrinews', image: git},
            {content: 'Agristores Warehouses', image: git},
            {content: 'Agristores Digital Information and Information Platforms', image: git},
            {content: 'Agristores Logistics and Transport Networks', image: git}
        ],
        departments: [
            {content: 'CCO', description: 'Chief Coordination Officer', image: cco},
            {content: 'CIO', description: 'Chief Internal Officer', image: cio},
            {content: 'CEO', description: 'Chief Executive Officer', image: ceo},
            {content: 'COO', description: 'Chief Operations Officer', image: coo},
            {content: 'CTO', description: 'Chief Technology Officer', image: cto}
        ],
        featured: {
            vegetables: {
                content: {},
                description: 'vegetables'
            },
            fruits: {
                content: {},
                description: 'fruits'
            },
            beverages: {
                content: {},
                description: 'beverages'
            },
            liveTreesAndPlants: {
                content: {},
                description: 'Live trees and plants'
            },
            AnimalProducts: {
                content: {},
                description: 'Animal Products'
            }
        },
        forms: {
            executive: {
                name: {
                    content: 'name',
                    placeholder: 'BLAIR'
                },
                post: {
                    content: 'department',
                    placeholder: 'CEO'
                }
            },
            feature: {
                id: {
                    content: 'ID',
                    placeholder: 'ID002'
                },
                feature: {
                    content: 'feature',
                    placeholder: 'VEGETABLES'
                }
            },
            subsidiary: {
                id: {
                    content: 'ID',
                    placeholder: 'ID002'
                },
                subsidiary: {
                    content: 'subsidiary',
                    placeholder: 'AGRIPAY'
                }
            }
        },
        records: {},
        feedback: {}
    }
  }
  handleLaunch = () => {
    this.setState({ showMenu: true})
    this.setState({ showTopBar: true})
    this.setState({ showRoot: false})
  }

  render(){
      return (
        <View
            style={styles.root}
        >
            <ScrollView>
                <Separator separatorText={this.state.separators.departments}></Separator>
                <View style={{flexDirection : 'row', backgroundColor: '#555', overflowX: 'scroll'}}>
                {this.state.showDepartments &&
                    this.state.departments.map((department) => <Card itemHeading={department.content} itemDescription={department.description} itemImage={department.image}></Card>)
                }
                </View>
                <Separator separatorText={this.state.separators.diversification}></Separator>
                <View style={{flexDirection : 'row', backgroundColor: '#555', overflowX: 'scroll'}}>
                {this.state.showDiversification &&
                    this.state.diversifications.map((diversification) => <Card itemHeading={diversification.content} itemImage={diversification.image}></Card>)
                }
                </View>
            </ScrollView>
        </View>
      )
  }
}
const styles = StyleSheet.create({
    root: {
        width: '50vw',
        height: '100vh',
        backgroundColor: '#0f0'
    }
})
